import React, { useState } from 'react'

import 'bootstrap/dist/css/bootstrap.min.css';



function Billform() {
    const [succ, setSucc]=useState(false);
    const Result = () => {
        return (
          <p>Refugee Registered Successfuly.</p>
        )
      }
  const [details, setdetails] = useState({
    G_body: "",
    Revenue: "",
    Services: "",
    camp: ""

  })

   function Handlechange (e){
    const name = e.target.name;
    const value = e.target.value;
    setdetails (prev => (
        {...prev, [name]: value}
    ))
   }

function handleClick (){
    // setSucc(!succ)
   
}   
   function Handlesubmit (event){
     event.preventDefault();
    setSucc (true)
    event.target.reset();
   
   }
  return (
    <div>
      <div className="maincontainer">
         
         
         <div class="container">
 
               <div class="card bg-light">
               <article class="card-body mx-auto" style={{maxWidth: "500px"}}>
                   <h4 class="card-title mt-3 text-center">Register A Refugee</h4>
                  
                  
                   <p class="divider-text">
                       <span class="bg-light">-</span>
                   </p>
                 

                   <form onSubmit={Handlesubmit}>

                         {/* g body */}

                   <div class="form-group input-group">
                       <div class="input-group-prepend">
                           <span class="input-group-text"> <i class="fa fa-user"></i> </span>
                       </div>
                       <input name="" class="form-control" placeholder="Enter Government Body" type="text"
                         Name="G_body"
                         value={details.G_body}
                         onChange={Handlechange}
                        />
                   </div> 
                       
                       {/* Revenue */}

                   <div class="form-group input-group">
                       <div class="input-group-prepend">
                           <span class="input-group-text"> <i class="fa fa-user"></i> </span>
                       </div>
                       <input name="" class="form-control" placeholder="Revenue Allocatted" type="number"
                         Name="Revenue"
                         value={details.Revenue}
                         onChange={Handlechange}
                        />
                   </div> 

                    {/* sevices */}

                   <div class="form-group input-group">
                       <div class="input-group-prepend">
                           <span class="input-group-text"> <i class="fa fa-envelope"></i> </span>
                       </div>
                       <input name="" class="form-control" placeholder="Service Provided" type="text" 
                         Name="Services"
                         value={details.Services}
                         onChange={Handlechange}
                       />
                   </div>
                       
                   
                   
                   {/* camp */}

                   <div class="form-group input-group">
                       <div class="input-group-prepend">
                           <span class="input-group-text"> <i class="fa fa-lock"></i> </span>
                       </div>
                       <input class="form-control" placeholder="Camp Name" type="number"
                         Name="camp"
                         value={details.camp}
                         onChange={Handlechange}
                        />
                   </div>  


                   <div class="form-group">
                       {succ ? <Result/> : ""}
                       <button type="submit" class="btn btn-primary btn-block" onClick={handleClick}> Register  </button>
                   </div>     


                                                                                   
               </form>
               </article>
               </div> 
               
               </div> 
            
       </div>
    </div>
  )
}

export default Billform
