import React, { createContext, useEffect, useState} from 'react'
import Axios from "axios"

const Campscontext = createContext(); // camps
const Ngocontext = createContext() ; // ngo
const Usercontext =  createContext(); // user

var details;
 export function  useuser(detail){
  details =detail;
  return (Usercontext)
}

// camp
export function usecamps(){

    return (Campscontext)
    
}

// ngo

export function usengo(){

    return (Ngocontext)
    
}

// main function

export function Kampsprovider({children})
{
  const url = "http://localhost:3001";
    // camps
  const [campslist, setcampslist] = useState([])
  
   useEffect(  ()=> {

    Axios.get( `${url}/camps`)
    
  .then (response=>{
    
  setcampslist(response.data)
   
  })
  .catch (err=>{
    console.log(err);
  })

 
},[])

// ngo
const [ngoslist, setngoslist] = useState([])


useEffect(()=> {
Axios.get( `${url}/ngo`)
.then ((response)=>{

  setngoslist(response.data)
 
})
.catch (err=>{
  console.log(err);
})

},[])


// USER ONLY
const [data, setdata] = useState ();

useEffect(()=>{

  Axios.post (`${url}/login`, {

     email : details.email,
    password:  details.password,
}
)
.then((res)=>{
    setdata(res) 
  
})
.catch (err=>{
  console.log(err);
})


},[data])

 





    return(
      <Campscontext.Provider   value={campslist}  >
         <Usercontext.Provider value={data} >
            <Ngocontext.Provider value={ngoslist}>

            {children}

            </Ngocontext.Provider>
            </Usercontext.Provider>
        </Campscontext.Provider>
    )
}
