import React, { useEffect, useState } from 'react'
import Ngoscard from '../components/Ngoscard'
import Ngosform from '../components/Ngosform'
import Axios from "axios"

function Ngos() {

  const [ngoslist, setngoslist] = useState([])
  const url = "http://localhost:3001";

useEffect(()=> {
  Axios.get( `${url}/camps`)
  .then ((response)=>{
    setngoslist(response.data);
   
  })
 
},[])


  return (
    <div>
     <h1>NGO's Center</h1>
     <div className='cards' >

       {ngoslist.map((ngo)=>
 < Ngoscard {...ngo}/>
      )}
     
     </div>
     <Ngosform/>

    </div>
  )
}

export default Ngos
